import React from 'react';
import { motion } from 'framer-motion'; // Import motion from Framer Motion
import './Offers.css';

const listVariants = {
  hidden: { opacity: 0, y: 20 }, // Initial hidden state
  visible: { opacity: 1, y: 0, transition: { duration: 1, delay: 0.2 } }, // Visible state with animation
};

const WhatWeOffer = () => {
  return (
    <div className="what-we-offer">
      <h2>WHAT WE OFFER</h2>
      <ul>
        <motion.li variants={listVariants} initial="hidden" animate="visible">
          Immutable and tamper-proof record keeping ensuring data integrity.
        </motion.li>
        <motion.li variants={listVariants} initial="hidden" animate="visible">
          Transparent and traceable transactions with a public ledger.
        </motion.li>
        <motion.li variants={listVariants} initial="hidden" animate="visible">
          Decentralized system reducing reliance on a central authority.
        </motion.li>
        <motion.li variants={listVariants} initial="hidden" animate="visible">
          Enhanced security with cryptographic encryption of data.
        </motion.li>
        <motion.li variants={listVariants} initial="hidden" animate="visible">
          Streamlined and efficient transaction processes reducing delays and costs.
        </motion.li>
      </ul>
    </div>
  );
};

export default WhatWeOffer;
